/**
 * @description Module dependencies
 */
import { Request, Response } from 'express';
/**
 * Transforms response to a specific format
 * @param responseCode
 * @param responseText
 * @param payload
 */
declare function transformResponse(responseCode: number, responseText: string, payload: {}): {
    responseCode: number;
    responseText: string;
    payload: {};
};
/**
*
* @param errors {Array}
* @returns {string}
* @description transforms errors generated by express validator to a single message string with each message separated by '|'
*/
declare function transformExpressValidationErrors(errors: any): string;
/**
 * @param req {request object}
 * @param res {response object}
 * @returns {response object} | boolean
 */
declare function validateRequest(req: Request, res: Response): false | Response<any, Record<string, any>>;
export { transformResponse, transformExpressValidationErrors, validateRequest };
